mapper.select:
SELECT * FROM ( SELECT a.*, rownum rnum FROM ( SELECT pb.purchase_num, pb.mem_num, pb.purchase_price, pb.purchase_regDate, pb.purchase_deadline, s.item_size, i.item_num, i.item_brand, i.item_name, i.item_photo1, i.item_photo1name FROM purchase_bid pb JOIN item i ON pb.item_num=i.item_num JOIN item_size s ON pb.item_sizenum=s.item_sizenum WHERE pb.mem_num=#{mem_num}
ORDER BY pb.purchase_regDate DESC) a)
WHERE rnum >= #{start} AND rnum <= #{end}

SELECT * FROM ( SELECT a.*,rownum rnum FROM ( SELECT t.trade_num, t.seller_num, d.trade_state, d.trade_regDate, d.trade_price, md.mem_name, i.item_num, s.item_size, i.item_brand, i.item_name, i.item_photo1, i.item_photo1name FROM trade t LEFT OUTER JOIN trade_detail d ON t.trade_num = d.trade_num JOIN item i ON i.item_num=t.item_num JOIN item_size s on d.item_sizenum=s.item_sizenum JOIN member_detail md on md.mem_num=t.seller_num WHERE buyer_num = #{mem_num}
ORDER BY d.trade_regDate DESC)a )
WHERE rnum >= #{start} AND rnum <= #{end}

SELECT * FROM ( SELECT a.*, rownum rnum FROM ( SELECT sb.sale_num, sb.mem_num, sb.sale_price, sb.sale_regDate, sb.sale_deadline, i.item_num, s.item_size, i.item_brand, i.item_name, i.item_photo1, i.item_photo1name FROM sale_bid sb JOIN item i ON sb.item_num=i.item_num JOIN item_size s ON sb.item_sizenum=s.item_sizenum WHERE sb.mem_num=#{mem_num}
ORDER BY sb.sale_regDate DESC) a)
WHERE rnum >= #{start} AND rnum <= #{end}

SELECT * FROM ( SELECT a.*,rownum rnum FROM ( SELECT t.trade_num, t.buyer_num, d.trade_state, d.trade_regDate, d.trade_price, md.mem_name, s.item_size, i.item_num, i.item_brand, i.item_name, i.item_photo1, i.item_photo1name FROM trade t LEFT OUTER JOIN trade_detail d ON t.trade_num = d.trade_num JOIN item i ON i.item_num=t.item_num JOIN item_size s on d.item_sizenum=s.item_sizenum JOIN member_detail md on md.mem_num=t.buyer_num WHERE seller_num = #{mem_num}
ORDER BY d.trade_regDate DESC)a )
WHERE rnum >= #{start} AND rnum <= #{end}
